// scripts/deploy.js
const { ethers } = require('hardhat');

async function main() {

    const ContractXOrder = await ethers.getContractAt('RemoteOrder', '0x111f2a0b83f8FEca2660B006a911B6C457863a34');
    const contractxOrderRes = await ContractXOrder.remoteOrder("", { value: 100 })
    console.log('Call ContractXOrder.remoteOrder result:', contractxOrderRes);
}

main()
    .then(() => process.exit(0))
    .catch((error) => {
        console.error(error);
        process.exit(1);
    });