// scripts/deploy.js
const { ethers } = require('hardhat');

async function main() {

    const ContractXOrder = await ethers.getContractFactory('RemoteOrder');
    const contract = ContractXOrder.attach("0x96502ef03DeCD4aabd368cC46f5c2C22A645948C");

    const contractxOrderRes = await contract.remoteOrder("", { value: ethers.utils.parseUnits('100','wei') })
    // const contractxOrderRes = await contract.remoteOrderDecoded("", { value: ethers.utils.parseUnits('1000','wei') })
    // const contractxOrderRes = await contract.generateId("0x1932978B1Ee86aD07E3e324ACc1641DBf14dbF59", "1")
    console.log('Call ContractXOrder.remoteOrder result:', contractxOrderRes);
}

main()
    .then(() => process.exit(0))
    .catch((error) => {
        console.error(error);
        process.exit(1);
    });